import React from "react";
import { Routes, Route } from "react-router-dom";
import Home from "./pages/Home";
import Profile from "./pages/Profile";
import Settings from "./pages/Settings";
import Sidebar from "./components/Sidebar";
import Rightbar from "./components/Rightbar";
import "./App.css";
import { useMoralis } from "react-moralis";
import { ConnectButton, Icon } from "web3uikit";

const App = () => {
  const { isAuthenticated, Moralis } = useMoralis();

  return (
    <>
    {isAuthenticated ? (
      <div className="page">
        <div className="sideBar">
          <Sidebar />
          <div
              className="logout"
              onClick={() => {
                Moralis.User.logOut().then(() => {
                  window.location.reload();
                });
              }}
            >
              Logout
            </div>
        </div>
        <div className="mainWindow">
          <Routes>
             <div
                  style={{
                    height: '90vh',
                    transform: 'scale(1)',
                    display: "flex",
                    justifyContent: "center",
                    textAlign: "center"
                  }}
                >
                  <div>
                    <Modal
                      cancelText="Discard Changes"
                      id="regular"
                      isVisible
                      okText="Save Changes"
                      onCancel={function noRefCheck(){}}
                      onCloseButtonPressed={function noRefCheck(){}}
                      onOk={function noRefCheck(){}}
                      title={<div style={{display: 'flex', gap: 10}}><Icon fill="#68738D" size={28} svg="edit"/><Typography color="#68738D" variant="h3">Edit Nickname</Typography></div>}
                    >
                      <div
                        style={{
                          padding: '20px 0 20px 0'
                        }}
                      >
                        <Input
                          label="Nickname"
                          width="100%"
                        />
                      </div>
                    </Modal>
                  </div>
                </div>
            <Route path="/" element={<Home />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/settings" element={<Settings />} />
          </Routes>
        </div>
        <div className="rightBar">
          <Rightbar />
        </div>
      </div>
      ) : (
        <div className="loginPage">
          <Icon fill="#ffffff" size={40} svg="twitter" />
          <ConnectButton />
        </div>

      )}
    </>
  );
};

export default App;
