import React from "react";
import "./TweetInFeed.css";
import golf from "../images/golf.png";
import canoe from "../images/canoe.png";
import { defaultImgs } from "../defaultimgs";
import { Icon } from "web3uikit";
import Web3 from 'web3';
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { CONTRACT_ABI, CONTRACT_ADDRESS } from '../config';

const TweetInProfile = ({profile, ethAddress}) => {

    const initialState = []

  const [tweets, setTweets] = useState([]);
  const [stars, setStars] = useState([]);
  const [info, setInfo] = useState([])
  const [account, setAccount] = useState();
  const web3 = new Web3(Web3.givenProvider || 'http://localhost:3000');
  const contractList = new web3.eth.Contract(CONTRACT_ABI, CONTRACT_ADDRESS);

  

  useEffect(() => {

    async function loadTweetProfile() {
      const accounts = await web3.eth.requestAccounts();
      setAccount(accounts[0])
      const tweetIdList = await contractList.methods.getUserTweet(ethAddress).call();
      setTweets(initialState)

      for(var i=0;i<tweetIdList[1];i++) {
        const tweet = await contractList.methods.getTweetwithInfo(tweetIdList[0][i]).call();
        const stars = await contractList.methods.getStar(tweetIdList[0][i]).call();
        let star = parseInt(stars)
        setTweets((tweets) => [...tweets, tweet]);
        setStars((stars) => [...stars,star]);

      }
    }

      loadTweetProfile();
    
  }, [ethAddress])


  const onClickStar = ( async (index) => {

    var whoStar = await contractList.methods.whoStar(index).call({from: account});
    console.log(whoStar)
    console.log(index)
    console.log(account)

    if (whoStar[0] === false) {
      await contractList.methods.star(index).send({from: account});

      let tmpStars = [...stars];
      tmpStars[index] += 1;
      setStars(tmpStars);
    } else {
      await contractList.methods.unStar(index).send({from: account});

      let tmpStars = [...stars];
      tmpStars[index] -= 1;
      setStars(tmpStars);
    }

  })

  return (
    
    <>
      {tweets.map((tweet, index) => {
          return (
            <>
              <div className="feedTweet">
                <Link to={`/profile/${tweet[2]}`} className="link"> 
                  <img src={tweet?.[6] === "" ? defaultImgs[0] : tweet?.[6]} className="profilePic" alt="Avatar"></img>
                </Link>
                <div className="completeTweet">
                  <div className="who">
                    {tweet?.[5] === "" ? <div>No name</div> : tweet?.[5]}
                      <div className="accWhen">{
                            `${tweet[2].slice(0, 4)}...${tweet[2].slice(38)} ·

                            ${tweet[4]}
                            `  
                          }
                      </div>
                  </div>
                  <div className="tweetContent">
                  {tweet[0]}
                  {tweet[1] !== "No Img" ? (
                          <img
                            src={tweet[1]}
                            className="tweetImg"
                          ></img>
                        ) : null}
                  </div>
                  <div className="interactions">
                    <div className="interactionNums">
                      <Icon fill="#3f3f3f" size={20} svg="messageCircle" />
                    </div>
                    <div className="interactionNums" onClick={(e) => {onClickStar(index)}}>
                      <Icon fill="red" size={20} svg="star" />
                      {stars[index]}
                    </div>
                    <div className="interactionNums">
                      <Icon fill="#3f3f3f" size={20} svg="matic" />
                    </div>
                  </div>
                </div>
              </div>
            </>
          );

        }
      ).reverse()}    
    </>
  );
};

export default TweetInProfile;
