import { TextArea, Icon, Input } from "web3uikit";
import "./Comment.css";
import "../components/TweetInFeed.css"
import TweetInFeed from "../components/TweetInFeed"
import { defaultImgs } from "../defaultimgs";
import { useParams, Link } from "react-router-dom";
import { CONTRACT_ABI, CONTRACT_ADDRESS } from '../config';
import Web3 from 'web3';
import { useState, useEffect } from "react"
  
  function Comment({ comment }) {

    const { id } = useParams()
    const [tweet, setTweet] = useState()

    const web3 = new Web3(Web3.givenProvider || 'http://localhost:3000');
    const contractList = new web3.eth.Contract(CONTRACT_ABI, CONTRACT_ADDRESS);

    useEffect(() => {
        async function loadTweet() {
            const tweet = await contractList.methods.getTweetwithInfo(id).call();
            setTweet(tweet)
        }

        loadTweet()
    }, [id])

    return (
        <div className="mainContent">
            <div>{console.log(tweet)}</div>
            <div className="feedTweet">
                <Link to={`/profile/${tweet[2]}`} className="link"> 
                  <img src={tweet?.[6] === "" ? defaultImgs[0] : tweet?.[6]} className="profilePic" alt="Avatar"></img>
                </Link>
                <div className="completeTweet">
                  <div className="who">
                    {tweet?.[5] === "" ? <div>No name</div> : tweet?.[5]}
                      <div className="accWhen">{
                            `${tweet[2].slice(0, 4)}...${tweet[2].slice(38)} Â·

                            ${tweet[4]}
                            `  
                          }
                      </div>
                  </div>
                  <div className="tweetContent">
                  {tweet[0]}
                  {tweet[1] !== "No Img" ? (
                          <img
                            src={tweet[1]}
                            className="tweetImg"
                          ></img>
                        ) : null}
                  </div>
                  <div className="interactions">
                    <div className="interactionNums">
                      <Icon fill="#3f3f3f" size={20} svg="messageCircle" />
                    </div>
                    <div className="interactionNums" >
                      <Icon fill="#ff1493" size={20} svg="star" />
                      {/* {stars[index]} */}23
                    </div>
                    <div className="interactionNums">
                      <Icon fill="#3f3f3f" size={20} svg="matic" />
                    </div>
                  </div>
                </div>
              </div>
            
            <div className="profileTweet">
                <img src={defaultImgs[0]} className="profilePic" alt="Avatar"></img>
                <div className="tweetBox">
                <TextArea
                    label=""
                    name="tweetTxtArea"
                    value="GM World"
                    type="text"
                    // onChange={(e) => setTweet(e.target.value)}
                    width="95%"
                ></TextArea>
                {/* {selectedFile && (
                    <img src={selectedFile} className="tweetImg"></img>
                )} */}
                <div className="imgOrTweet">
                    <div className="imgDiv" >
                        <input
                            type="file"
                            name="file"

                            style={{ display: "none"}}
                        />
                        <Icon fill="#1DA1F2" size={20} svg="image"></Icon>
                    </div>
                        <div className="tweetOptions">
                        {/* <div className="tweet" onClick={saveTweet}>Tweet</div> */}
                            <div className="tweet" style={{ backgroundColor: "#8247e5" }}>
                                <Icon fill="#ffffff" size={20} svg="eth" title="Tweet" />
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <TweetInFeed profile={false}/>
        </div>
    );
  }
  
  export default Comment;
