import React from "react";
import { Link, useParams } from "react-router-dom";
import './Profile.css';
import { defaultImgs } from "../defaultimgs";
import TweetInFeed from "../components/TweetInFeed";
import { useMoralis } from "react-moralis";
import { useEffect, useState } from "react";
import Web3 from 'web3';
import { CONTRACT_ABI, CONTRACT_ADDRESS } from '../config';


const Profile = () => {
  const { Moralis} = useMoralis();
  const user = Moralis.User.current();
  let { address } = useParams();
  const [profile, setProfile] = useState()
  const [name, setName] = useState()
  const web3 = new Web3(Web3.givenProvider || 'http://localhost:3000');
  const contractList = new web3.eth.Contract(CONTRACT_ABI, CONTRACT_ADDRESS);

  useEffect(() => {
    async function getProfile() {
      const info = await contractList.methods.getProfile(address).call();
      setProfile(info)
    }
    getProfile()
  },[])

  return (
    <>
    <div className="pageIdentify">Profile</div>
    <img className="profileBanner" src={profile?.[3] !== "" ? profile?.[3] : defaultImgs[1]}></img>
    <div className="pfpContainer">
      <img className="profilePFP" src={profile?.[2] !== "" ? profile?.[2] : defaultImgs[0]}></img>
      {profile?.[0] === "" ? (<div className="profileName">No Name</div>) : (<div className="profileName">{profile?.[0]}</div>)}
      <div className="profileWallet">{`${user.attributes.ethAddress.slice(0, 4)}...
            ${address.slice(38)}`}</div>
      <Link to="/settings">
          <div className="profileEdit">Edit profile</div>
      </Link>
      {profile?.[1] === "" ? 
        <div className="profileBio">
          No bio
        </div> : 
        <div className="profileBio">
          {profile?.[1]}
        </div>}
      <div className="profileTabs">
          <div className="profileTab">
          Your Tweets
          </div>
      </div>
    </div>
    <TweetInFeed profile={true} ethAddress={address}></TweetInFeed>
    
    </>
  );
};

export default Profile;
