import React from "react";
import "./TweetInFeed.css";
import golf from "../images/golf.png";
import canoe from "../images/canoe.png";
import { defaultImgs } from "../defaultimgs";
import { Icon } from "web3uikit";
import { useMoralis, useWeb3ExecuteFunction, useMoralisWeb3Api, useMoralisWeb3ApiCall } from "react-moralis";
import { useEffect, useState } from "react";

const TweetInFeed = ({ profile }) => {
  const [tweetArr, setTweetArr] = useState();
  const { Moralis, account } = useMoralis();

  const [tweets, setTweets] = useState([]);
  const [index, setIndex] = useState(0)

  // const contractProcessor = useWeb3ExecuteFunction();
  const { data, error, fetch, isFetching, isLoading } = useWeb3ExecuteFunction();

  let options = {
    contractAddress: "0x4dd88ed510486482D0d896faACb39F56C997c979",
    functionName: "getTweet",
    abi: [{
      "inputs": [
        {
          "internalType": "uint256",
          "name": "id",
          "type": "uint256"
        }
      ],
      "name": "getTweet",
      "outputs": [
        {
          "internalType": "string",
          "name": "",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "",
          "type": "string"
        },
        {
          "internalType": "address",
          "name": "",
          "type": "address"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    }],
    params: {
      id: index,
    }
  }

  
    async function getTweet() {
      // setIndex0)
      // while(inx<2) {
      
      await fetch({
        params: options,
        onSuccess: () => {
          console.log("success")
        },
        onError: () => {
          console.log('error')
        }
      })
      
      if(index!==0 && index<2) {
        setTweets([...tweets, data])
      }
      
      setIndex(index+1);
        
        
      console.log(tweets)
      console.log(index)
      
    // }
  }
  

  useEffect(() => {
    getTweet()
  },[tweets])


  // useEffect(() => {
  //   async function getTweets() {
  //     try {
  //       const Tweets = Moralis.Object.extend("Tweets");
  //       const query = new Moralis.Query(Tweets);
  //       if (profile) {
  //         query.equalTo("tweeterAcc", account);
  //       }
  //       const results = await query.find();

  //       setTweetArr(results);
  //       // console.log(results);
  //     } catch (error) {
  //       console.error(error);
  //     }
  //   }
  //   getTweets();
  // }, [profile]);

  return (
    <>
      {tweets.map((e) => {
        return (
          <>
            <div className="feedTweet">
              <img src={defaultImgs[0]} className="profilePic"></img>
              <div className="completeTweet">
                <div className="who">
                
                  <div className="accWhen">{
                        `${e[2].slice(0, 4)}...${e[2].slice(38)} · 
                       
                        `  
                      }
                      </div>
                </div>
                <div className="tweetContent">
                {e[0]}
                {e[1] && (
                        <img
                          src={e[1]}
                          className="tweetImg"
                        ></img>
                      )}
                </div>
                <div className="interactions">
                  <div className="interactionNums">
                    <Icon fill="#3f3f3f" size={20} svg="messageCircle" />
                  </div>
                  <div className="interactionNums">
                    <Icon fill="#3f3f3f" size={20} svg="star" />
                    12
                  </div>
                  <div className="interactionNums">
                    <Icon fill="#3f3f3f" size={20} svg="matic" />
                  </div>
                </div>
              </div>
            </div>
          </>
        );
      }).reverse()}

    </>
  );
};

export default TweetInFeed;
