import React from "react";
import "./Home.css";
import { defaultImgs } from "../defaultimgs";
import { TextArea, Icon, Input } from "web3uikit";
import { useState, useRef } from "react";
import TweetInFeed from "../components/TweetInFeed";
import { useMoralis, useWeb3ExecuteFunction } from "react-moralis";
import Web3 from 'web3';
import { CONTRACT_ABI, CONTRACT_ADDRESS } from '../config';
import { useEffect } from "react";


const Home = () => {

  const { Moralis } = useMoralis();
  const user = Moralis.User.current();

  const inputFile = useRef(null);
  const [selectedFile, setSelectedFile] = useState();
  const [theFile, setTheFile] = useState();
  const [tweet, setTweet] = useState();
  const [search, setSearch] = useState("")
  const [account, setAccount] = useState();
  const web3 = new Web3(Web3.givenProvider || 'http://localhost:3000');
  const contractList = new web3.eth.Contract(CONTRACT_ABI, CONTRACT_ADDRESS);

  useEffect(() => {
    const dateNow = new Date().toLocaleString() + ''
    console.log(typeof(dateNow))
    async function loadAccount() {
      const accounts = await web3.eth.requestAccounts();
      setAccount(accounts[0])
    }
    loadAccount()
  }, [])

  async function addTweet() {

    let img;
    if (theFile) {
      const data = theFile;
      const file = new Moralis.File(data.name, data);
      await file.saveIPFS();
      img = file.ipfs();
    }else{
      img = "No Img"
    }

    await contractList.methods.addTweet(tweet, img).send({from: account, value: 2000000000000000})
    window.location.reload();
  }

  const onImageClick = () => {
    inputFile.current.click();
  };

  const changeHandler = (event) => {
    const img = event.target.files[0];
    setTheFile(img);
    setSelectedFile(URL.createObjectURL(img));
  };

  return (
    <>
    <div className="pageIdentify">Home</div>
      
      <div className="mainContent">
        <Input
          label="Search Twitter"
          name ="Search Twitter"
          prefixIcon="search"
          labelBgColor="#141d26" 
          onChange={event => {
              setSearch(event.target.value)
              }
            }
          >
        </Input>
        <div className="profileTweet">
          <img src={user.attributes.pfp ? user.attributes.pfp : defaultImgs[0]} className="profilePic"></img>
          <div className="tweetBox">
            <TextArea
              label=""
              name="tweetTxtArea"
              value="GM World"
              type="text"
              onChange={(e) => setTweet(e.target.value)}
              width="95%"
            ></TextArea>
            {selectedFile && (
              <img src={selectedFile} className="tweetImg"></img>
            )}
            <div className="imgOrTweet">
              <div className="imgDiv" onClick={onImageClick}>
              <input
                  type="file"
                  name="file"
                  ref={inputFile}
                  onChange={changeHandler}
                  style={{ display: "none"}}
                />
                <Icon fill="#1DA1F2" size={20} svg="image"></Icon>
              </div>
              <div className="tweetOptions">
                {/* <div className="tweet" onClick={saveTweet}>Tweet</div> */}
                <div className="tweet" onClick={addTweet} style={{ backgroundColor: "#8247e5" }}>
                  <Icon fill="#ffffff" size={20} svg="eth" title="Tweet" />
                </div>
              </div>
            </div>
          </div>
        </div>
        <TweetInFeed searchText={search}/>
      </div>
    </>
  );
};

export default Home;
