import React from 'react'
import './CommentComponent.css';
import { Icon, Modal, Typography, Input } from "web3uikit";
import './TweetInFeed.css';

function CommentComponent() {
  const data = [
    {
      name: 'dung',
      createdAt: new Date('Sat Jul 16 2022 17:56:20 GMT+0700 (Indochina Time)'),
      tweeterAcc: '0x6bfe51e48f2193bea7a88ceffd5d8dfb58d190f2',
      content: 'Hellooo'
    }
  ];
  let tweetImg = 'https://scontent.fhan2-2.fna.fbcdn.net/v/t39.30808-1/281495229_1764542407211310_8174834509622889647_n.jpg?stp=cp0_dst-jpg_p80x80&_nc_cat=110&ccb=1-7&_nc_sid=7206a8&_nc_ohc=gxOWlODze64AX9PQfvv&_nc_ht=scontent.fhan2-2.fna&oh=00_AT-_GVYdPlbaLxrrLFchyXaTzpBCqlCpI3RmBkHYvZUY7A&oe=62DAB8DF'
  
  return (
    <div className='comment-container'>
      <img src={tweetImg} className="profilePic"></img>
      <div className="completeTweet">
          <div className="who">
            {data[0].name.slice(0, 6)}
            <div className="accWhen">
              {
                  `${data[0].tweeterAcc.slice(0, 4)}...${data[0].tweeterAcc.slice(38)} · 
                  ${data[0].createdAt.toLocaleString('en-us', { month: 'short' })}  
                  ${data[0].createdAt.toLocaleString('en-us', { day: 'numeric' })}
                  `  
              }
            </div>
          </div>
        <div className="tweetContent">
            {data[0].content}
        </div>
        <div className="interactions">
          <div className="interactionNums" onClick={() => {
              document.querySelector(".modal").style.display = "flex"
          }}>
            <Icon fill="#3f3f3f" size={20} svg="messageCircle" />
          </div>
          <div className="interactionNums">
            <Icon fill="#3f3f3f" size={20} svg="star" />
            12
          </div>
          <div className="interactionNums">
            <Icon fill="#3f3f3f" size={20} svg="matic" />
          </div>
        </div>
      </div>
    </div>
  )
}

export default CommentComponent
