import React from "react";
import "./TweetInFeed.css";
import golf from "../images/golf.png";
import canoe from "../images/canoe.png";
import { defaultImgs } from "../defaultimgs";
import { Icon, Modal, Typography, Input } from "web3uikit";
import { useMoralis } from "react-moralis";
import { useEffect, useState } from "react";

const PopupComment = () => {
  return(
    <>
      <div
        style={{
          height: '90vh',
          transform: 'scale(1)'
        }}
      >
      <div>
        <Modal
          cancelText="Discard Changes"
          id="regular"
          isVisible
          okText="Save Changes"
          onCancel={function noRefCheck(){}}
          onCloseButtonPressed={function noRefCheck(){}}
          onOk={function noRefCheck(){}}
          title={<div style={{display: 'flex', gap: 10}}><Icon fill="#68738D" size={28} svg="edit"/><Typography color="#68738D" variant="h3">Edit Nickname</Typography></div>}
        >
          <div
            style={{
              padding: '20px 0 20px 0'
            }}
          >
            <Input
              label="Nickname"
              width="100%"
            />
          </div>
        </Modal>
      </div>
    </div>
  </>
  )
};

export default PopupComment;
