import React from "react";
import "./TweetInFeed.css";
import golf from "../images/golf.png";
import canoe from "../images/canoe.png";
import { defaultImgs } from "../defaultimgs";
import { Icon } from "web3uikit";
import Web3 from 'web3';
import { useEffect, useState } from "react";

const TweetInFeed = ({ searchText }) => {

  const [tweets, setTweets] = useState([]);
  // const [search, setSearch] = useState([]);
  // const [count, setCount] = useState();

  const CONTRACT_ADDRESS = '0xd2d987eadbf20d0B2f8C685E5C4A00f53F3189DB';
  const CONTRACT_ABI = [
    {
      "inputs": [],
      "name": "getCounter",
      "outputs": [
        {
          "internalType": "uint256",
          "name": "",
          "type": "uint256"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "uint256",
          "name": "id",
          "type": "uint256"
        }
      ],
      "name": "getTweet",
      "outputs": [
        {
          "internalType": "string",
          "name": "",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "",
          "type": "string"
        },
        {
          "internalType": "address",
          "name": "",
          "type": "address"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "txt",
          "type": "string"
        }
      ],
      "name": "searchTweet",
      "outputs": [
        {
          "internalType": "uint256[]",
          "name": "",
          "type": "uint256[]"
        },
        {
          "internalType": "uint256",
          "name": "",
          "type": "uint256"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    }
  ];

  useEffect(() => {
    async function load() {
      const web3 = new Web3(Web3.givenProvider || 'http://localhost:3000');
      const contractList = new web3.eth.Contract(CONTRACT_ABI, CONTRACT_ADDRESS);
      
      const counter = await contractList.methods.getCounter().call();

      // const searchFound = await contractList.methods.searchTweet(searchText).call();
      // setSearch(searchFound[0])
      // setCount(searchFound[1])

      // setTweets([])
      for(var i=0;i<counter;i++) {
        const tweet = await contractList.methods.getTweet(i).call();
        setTweets((tweets) => [...tweets, tweet]);
      }

    }

    load();
  }, [])

  return (
    
    <>

      {tweets.map((tweet) => {
          return (
            <>
              <div className="feedTweet">
                <img src={defaultImgs[0]} className="profilePic"></img>
                <div className="completeTweet">
                  <div className="who">
                  
                    <div className="accWhen">{
                          `${tweet[2].slice(0, 4)}...${tweet[2].slice(38)} 
                        
                          `  
                        }
                        </div>
                  </div>
                  <div className="tweetContent">
                  {tweet[0]}
                  {tweet[1] !== "No Img" ? (
                          <img
                            src={tweet[1]}
                            className="tweetImg"
                          ></img>
                        ) : null}
                  </div>
                  <div className="interactions">
                    <div className="interactionNums">
                      <Icon fill="#3f3f3f" size={20} svg="messageCircle" />
                    </div>
                    <div className="interactionNums">
                      <Icon fill="#3f3f3f" size={20} svg="star" onClick={console.log('sfds')}/>
                      12
                    </div>
                    <div className="interactionNums">
                      <Icon fill="#3f3f3f" size={20} svg="matic" />
                    </div>
                  </div>
                </div>
              </div>
            </>
          );

        }
      ).reverse()}    
    </>
  );
};

export default TweetInFeed;
